mapper.select:
select 

from t0201worktime
where yearMonth = #{yearMonth,jdbcType=CHAR}
  and workerName = #{workerName,jdbcType=NVARCHAR}
  and departmentId = #{departmentId,jdbcType=VARCHAR}

mapper.delete:
delete from t0201worktime
where yearMonth = #{yearMonth,jdbcType=CHAR}
  and workerName = #{workerName,jdbcType=NVARCHAR}
  and departmentId = #{departmentId,jdbcType=VARCHAR}
