mapper.select:
select 

from t0103productbilityworkers
where yearMonth = #{yearMonth,jdbcType=CHAR}
  and departmentId = #{departmentId,jdbcType=VARCHAR}
  and wokerName = #{wokerName,jdbcType=NVARCHAR}

mapper.delete:
delete from t0103productbilityworkers
where yearMonth = #{yearMonth,jdbcType=CHAR}
  and departmentId = #{departmentId,jdbcType=VARCHAR}
  and wokerName = #{wokerName,jdbcType=NVARCHAR}

mapper.insert:
insert into t0103productbilityworkers (yearMonth, departmentId, wokerName, 
  workTime, createtime, createuser, 
  updatetime, updateuser, delflg
  )
values (#{yearMonth,jdbcType=CHAR}, #{departmentId,jdbcType=VARCHAR}, #{wokerName,jdbcType=NVARCHAR}, 
  #{workTime,jdbcType=NUMERIC}, #{createtime,jdbcType=TIMESTAMP}, #{createuser,jdbcType=VARCHAR}, 
  #{updatetime,jdbcType=TIMESTAMP}, #{updateuser,jdbcType=VARCHAR}, #{delflg,jdbcType=BIT}
  )

mapper.update:
