    updatetime = #{updatetime,jdbcType=TIMESTAMP},


    updateuser = #{updateuser,jdbcType=VARCHAR},


    delflg = #{delflg,jdbcType=BIT},


where yearMonth = #{yearMonth,jdbcType=CHAR}
  and departmentId = #{departmentId,jdbcType=VARCHAR}
  and wokerName = #{wokerName,jdbcType=NVARCHAR}

update t0103productbilityworkers
