mapper.select:
select 

from t0104productbilityitems
where yearMonth = #{yearMonth,jdbcType=CHAR}
  and departmentId = #{departmentId,jdbcType=VARCHAR}
  and itemName = #{itemName,jdbcType=NVARCHAR}
  and technicsCD = #{technicsCD,jdbcType=NVARCHAR}

mapper.delete:
delete from t0104productbilityitems
where yearMonth = #{yearMonth,jdbcType=CHAR}
  and departmentId = #{departmentId,jdbcType=VARCHAR}
  and itemName = #{itemName,jdbcType=NVARCHAR}
  and technicsCD = #{technicsCD,jdbcType=NVARCHAR}
